basement_position = nil
input.split("").each_with_index.reduce(0) do |memo, (character, i)|
  if memo < 0
    basement_position = i
    break
  end
  step = character == "(" ? 1 : character == ")" ? -1 : 0
  memo + step
end
puts basement_position
